mapper.select:
select ID id, MESSAGE_NAME name, MESSAGE_CONTENT content, MESSAGE_PHONE phone, MESSAGE_EMAIL email, REPLY_ID replyId, CREATE_TIME createTime from tb_message tm where tm.DELETE_FLAG = '1'
and tm.MESSAGE_NAME like concat('%',#{name},'%')
and tm.MESSAGE_CONTENT like concat('%',#{content},'%')
and tm.REPLY_ID = #{replyId}
and tm.REPLY_ID is null
and tm.CREATE_TIME > #{startTime}
and tm.CREATE_TIME < #{endTime}
ORDER BY tm.CREATE_TIME DESC

mapper.update:
update tb_message
set DELETE_FLAG = '0'
where ID = #{id} or REPLY_ID = #{id}
