mapper.insert:
insert into tb_blog_type(
BLOGTYPE_NAME,
BLOGTYPE_IMG,
BLOGTYPE_DESCRIPTION
)values (
#{blogTypeName},
#{blogTypeImg},
#{blogTypeDescription}
)

mapper.update:
UPDATE tb_blog_type
SET BLOGTYPE_NAME = #{blogTypeName},
BLOGTYPE_IMG = #{blogTypeImg},
BLOGTYPE_DESCRIPTION = #{blogTypeDescription}
WHERE ID = #{id}

mapper.select:
select count(id)
from tb_blog
where BLOG_TYPE = #{id}
