mapper.insert:
insert into tb_blog_type(
BLOGTYPE_NAME,
BLOGTYPE_IMG,
BLOGTYPE_DESCRIPTION
)values (
#{blogTypeName},
#{blogTypeImg},
#{blogTypeDescription}
)

mapper.update:
UPDATE tb_blog
SET BLOGTYPE_NAME = #{blogTypeName},
BLOGTYPE_IMG = #{blogTypeImg},
BLOGTYPE_DESCRIPTION = #{blogTypeDescription}
WHERE ID = #{id}

UPDATE tb_blog
SET DELETE_FLAG = '0'
WHERE ID = #{id}
